            //m_LHandlers = new PacketHandler<ClientConnection>[0x30];
            _levels = new Dictionary<int, PacketHandler<ClientConnection>[]>();

            once2 = true; //если false, do not repeat again
            once3 = true; //если false, do not repeat again
