        public NP_SCUnitVisualOptionsPacket_0x01C0(ClientConnection net, Character chr, string cmd, string cmd2) : base(01, 0x01C0)
        {
            //1.0.1406
            //SCUnitVisualOptionsPacket
            //4000 DD01 C001 FF091A00 0B00 4A757374746F636865636B 1000 6368617261637465725F6F7074696F6E 1300 675F686964655F7475746F7269616C20310D0A 14000000
            //3B0E DD01 C001 FF091A00 0B00 4A757374746F636865636B 0B00 6B65795F62696E64696E67 130E  140E0000

            ns.Write((int)chr.CharacterId); //type d
            ns.WriteUTF8Fixed(chr.CharName, chr.CharName.Length); //name S
            ns.WriteUTF8Fixed(cmd, cmd.Length);
            ns.WriteUTF8Fixed(cmd2, cmd2.Length);
            ns.Write((int)0x14);
        }
